import axios from 'axios'

const USER_API_URL = "https://localhost:5000/users";

class UserService{
    //get all methods related to user crud

    getUsers(){
        return axios.get(USER_API_URL)
    }

    getUserById(id){
        return axios.get(USER_API_URL + '/' + id)
    }

    createUser(user){
        return axios.post(USER_API_URL, user)
    }

    updateUser(id){
        return axios.put(USER_API_URL + '/' + id)
    }

    deleteUser(id){
        return axios.delete(USER_API_URL + '/' + id)
    }

}

export default new UserService()
